import react, {useState,useEffect} from 'react'
import {auth} from '../../firebase'
import {toast} from 'react-toastify'



const ConcRegistro = ({history}) => {
const [password,setPassword] = useState("")
const [email,setEmail] = useState("")
useState(()=>{setEmail(window.localStorage.getItem("emailRegistro"))},[])

const handleSubmit = async (e) => {
 e.preventDefault()   
const concregistroForm = () => (
<form onSubmit={handleSubmit}>Formulario Registro
<input type="email" className="form-control" value = {email} disabled autoFocus></input>
<input type="passord" className="form-control" value = {password} onChange={(e) => setPassword(e.target.value)} autoFocus placeholder="Senha"></input>

<button type ="submit" className="btn btn-raised">Registrar</button>
</form>
)
return (
        <div className="container p-5">
           <div className="row">
               <div className=".col-md-6.offset-md-3">
                   <h4>Conclus√£o de registro</h4> 
                   {concregistroForm()}
               </div>
           </div>
        </div>
     );
     }






    }
    export default ConcRegistro;
