import react, {useState} from 'react'
import {auth} from '../../firebase'
import {toast} from 'react-toastify'



const Login = () => {
const [email,setEmail,password,setPassword] = useState("")
const handleSubmit = async (e) => {
 e.preventDefault()
 console.log("ENV ===>",process.env.REACT_APP_REGISTER_REDIRECT_URL);
 const config =  {
     url: process.env.REACT_APP_REGISTER_REDIRECT_URL,
     handleCodeInApp: true,
 }

await auth.sendSignInLinkToEmail(email, config);
toast.success(`email enviado para ${email}. clique no link para completar o seu registro.`)
window.localStorage.setItem('emailRegistro', email)
setEmail("");
}     
const loginForm = () => (
<form onSubmit={handleSubmit}>Area de Login
<input type="email" className="form-control" value = {email} onChange= {e => setEmail(e.target.value)} autoFocus placeholder = "seu email"></input>
<input type="password" className="form-control" value = {password} onChange= {e => setPassword(e.target.value)}  ></input>


<br />
<button onClick = {handleSubmit} type ="submit" className="btn btn-raised" disabled={!email || password.length < 6}>Logar</button>
</form>
)
return (
        <div className="container p-5">
           <div className="row">
               <div className=".col-md-6.offset-md-3">
                   <h4>Login</h4> 
                   {loginForm()}
               </div>
           </div>
        </div>
     );
     }





export default Login;
