    let history = useHistory()

    useEffect(()=>{
        const interval = setInterval(()=>{
        setCount((currentCount) => --currentCount);
        }, 1000);

        count === 0 && history.push('/')
        return () => clearInterval(interval);
    },[count])

return (<div className ="container p-5 text-center">
